import React from 'react';
import { Pane, Heading } from 'evergreen-ui'

type InfoCardProps = {
    title?: string
    body?: any
    children?: any
}

export default function InfoCard({ title, body, children }: InfoCardProps) {

    return (

        <Pane>
            {typeof title === 'string' &&
                <Pane borderBottom="default"><Heading>{title}</Heading></Pane>
            }
            <Pane>
                {body}
            </Pane>
        </Pane>


    );

}
