import React, { useEffect } from 'react';
import { Button, Heading, Paragraph, TextInput } from 'evergreen-ui'
import Page from '../Components/Page';
import InfoCard from '../Components/InfoCard';

export default function Participate() {

    const [response, setResponse] = React.useState('')
    const [team, setTeam] = React.useState(["", false]);
    const [person1, setPerson1] = React.useState(["", "", ""])
    const [person2, setPerson2] = React.useState(["", "", ""])
    const [person3, setPerson3] = React.useState(["", "", ""])


    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
            team: `${team[0]}`, official: team[1],
            name1: `${person1[0]}`, email1: `${person1[1]}`, number1: `${person1[2]}`,
            name2: `${person2[0]}`, email2: `${person1[1]}`, number2: `${person1[2]}`,
            name3: `${person3[0]}`, email3: `${person1[1]}`, number3: `${person1[2]}`,

        })
    };

    function callApi() {
        fetch("http://localhost:9000/mail", requestOptions)
            .then(res => res.text())
            .then(res => { console.log(res); setResponse(res); })
    }


    return (
        <Page>
            <InfoCard title="Participant 1">
                <Paragraph>
                    <Heading>Name </Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([e.target.value, person1[1]])}
                    />
                    <Heading marginTop={12}>Email</Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([person1[0], e.target.value])}
                    />
                    <Heading marginTop={12}>Email</Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([person1[0], e.target.value])}
                    />
                </Paragraph>
            </InfoCard>
            <InfoCard title="Participant 2 (Optional)">
                <Paragraph>
                    <Heading>Name </Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([e.target.value, person1[1]])}
                    />
                    <Heading marginTop={12}>Email</Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([person1[0], e.target.value])}
                    />
                </Paragraph>
            </InfoCard>
            <InfoCard title="Participant 3 (Optional)">
                <Paragraph>
                    <Heading>Name </Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([e.target.value, person1[1]])}
                    />
                    <Heading marginTop={12}>Email</Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([person1[0], e.target.value])}
                    />
                </Paragraph>
            </InfoCard>
            <Button marginTop={12} onClick={() => callApi()}>Send</Button>
            <p>Hey: {response}</p>
        </Page>
    );
}
