import React, { useEffect } from 'react';
import { Button, Heading, Paragraph, TextInput } from 'evergreen-ui'
import Page from '../Components/Page';
import InfoCard from '../Components/InfoCard';

export default function Participate() {

    const [response, setResponse] = React.useState('')
    const [team, setTeam] = React.useState()
    const [person1, setPerson1] = React.useState(["", ""])
    const [person2, setPerson2] = React.useState(["", ""])
    const [person3, setPerson3] = React.useState(["", ""])


    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: `${person1[0]}`, email: `${person1[1]}` })
    };

    function callApi() {
        fetch("http://localhost:9000/mail", requestOptions)
            .then(res => res.text())
            .then(res => { console.log(res); setResponse(res); })
    }


    return (
        <Page>
            <InfoCard>
                <Paragraph>
                    <Heading>Name </Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([e.target.value, person1[1]])}
                    />
                    <Heading>Email</Heading>
                    <TextInput
                        onChange={(e: any) => setPerson1([person1[0], e.target.value])}
                    />
                </Paragraph>
                <Button onClick={() => callApi()}>Send</Button>
            </InfoCard>
            <p>Hey: {response}</p>
        </Page>
    );
}
