import React from 'react';
import { Pane, Heading } from 'evergreen-ui'

type InfoCardProps = {
    title?: string
    body?: any
    children?: any
}

export default function InfoCard({ title, body, children }: InfoCardProps) {

    return (
        <Pane border="default" flex={1} flecDirection alignItems="center" display="flex" justifyContent="center" maxWidth="600px">
            <Pane>
                {typeof title === 'string' &&
                    <Pane borderBottom="default">
                        <Heading size={600}>{title.toUpperCase()}</Heading>
                    </Pane>
                }
                {children}
            </Pane>
        </Pane>


    );

}
